package com.bridgelabz;
public class Secondpattern {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int i;
        for (i = 1; i < 6; i++) {
            int j;
            for (j = 1; j <= i; j++) {
                System.out.print(j);
            }

            System.out.println("");


        }
    }

}
